import React, { useState } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import { useSpeechSynthesis } from 'react-speech-kit';

const App = () => {
  const [listening, setListening] = useState(false);
  const { transcript, resetTranscript } = useSpeechRecognition();
  const { speak } = useSpeechSynthesis();
  const [sentence, setSentence] = useState('');

  const toggleListening = () => {
    if (!listening) {
      SpeechRecognition.startListening({ continuous: true, language: 'en-IN' });
    } else {
      SpeechRecognition.stopListening();
    }
    setListening(!listening);
  };

  const speakText = (text) => {
    speak({ text });
  };

  const handleInputChange = (event) => {
    resetTranscript();
    // No need to speak immediately when typing
    setSentence(event.target.value);
  };

  const handleSpeakClick = () => {
    // Speak the sentence entered in the input box
    speakText(sentence);
  };

  return (
    <div className='container'>
      <h1>Voice Recognition</h1>
      <div className="controls">
        <button onClick={toggleListening}>{listening ? 'Stop' : 'Start'} Listening</button>
        <button onClick={resetTranscript}>Reset</button>
        <button onClick={handleSpeakClick} disabled={!sentence}>Speak Sentence</button>
      </div>
      <div className="text-area-container">
        <textarea className="text-area" value={transcript} onChange={handleInputChange} placeholder="Speak or type here..." />
      </div>
      <div className="input-box">
        <input type="text" value={sentence} onChange={handleInputChange} placeholder="Enter a sentence to speak" />
      </div>
    </div>
  );
};

export default App;
